def findMaxArray(arr):
  if len(arr) == 0:
    return None  # Handle the case of an empty array

  rightPointer = len(arr) - 1

  max = float('-inf')  # Start with negative infinity as the initial maximum value

  while leftPointer < rightPointer:
    if arr[leftPointer] > max:
      max = arr[leftPointer]

    if arr[rightPointer] > max:
      max = arr[rightPointer]

    leftPointer += 1
    rightPointer -= 1

  return max
}
